import React from 'react';
import { useLanguage } from '../utils/LanguageContext';

const IconBasedNavigation = ({ onDistrictSelect, districts }) => {
  const { language, speak, getDistrictName } = useLanguage();

  const majorDistricts = [
    { name: 'CHENNAI', icon: 'üèôÔ∏è', color: '#e74c3c' },
    { name: 'COIMBATORE', icon: 'üè≠', color: '#3498db' },
    { name: 'MADURAI', icon: 'üèõÔ∏è', color: '#9b59b6' },
    { name: 'SALEM', icon: 'üåæ', color: '#27ae60' },
    { name: 'VELLORE', icon: 'üè∞', color: '#f39c12' },
    { name: 'TIRUCHIRAPPALLI', icon: '‚õ©Ô∏è', color: '#e67e22' }
  ];

  const handleDistrictClick = (district) => {
    const announcement = language === 'ta'
      ? `${getDistrictName(district)} ‡ÆÆ‡Ææ‡Æµ‡Æü‡Øç‡Æü‡ÆÆ‡Øç ‡Æ§‡Øá‡Æ∞‡Øç‡Æ®‡Øç‡Æ§‡ØÜ‡Æü‡ØÅ‡Æï‡Øç‡Æï‡Æ™‡Øç‡Æ™‡Æü‡Øç‡Æü‡Æ§‡ØÅ`
      : `${district} district selected`;

    speak(announcement);
    onDistrictSelect(district);
  };

  return (
    <div style={{ marginTop: '30px' }}>
      <h3 style={{ 
        textAlign: 'center', 
        marginBottom: '20px',
        fontSize: '1.5rem',
        color: '#2c3e50'
      }}>
        {language === 'ta' ? '‡ÆÆ‡ØÅ‡Æï‡Øç‡Æï‡Æø‡ÆØ ‡ÆÆ‡Ææ‡Æµ‡Æü‡Øç‡Æü‡Æô‡Øç‡Æï‡Æ≥‡Øç' : 'Major Districts'}
      </h3>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
        gap: '15px',
        padding: '20px'
      }}>
        {majorDistricts.map(district => (
          <button
            key={district.name}
            onClick={() => handleDistrictClick(district.name)}
            style={{
              background: district.color,
              color: 'white',
              border: 'none',
              borderRadius: '15px',
              padding: '20px',
              cursor: 'pointer',
              fontSize: '16px',
              fontWeight: 'bold',
              textAlign: 'center',
              transition: 'transform 0.3s',
              boxShadow: '0 4px 10px rgba(0,0,0,0.2)'
            }}
            onMouseOver={(e) => e.target.style.transform = 'scale(1.05)'}
            onMouseOut={(e) => e.target.style.transform = 'scale(1)'}
          >
            <div style={{ fontSize: '2rem', marginBottom: '10px' }}>
              {district.icon}
            </div>
            <div style={{ fontSize: '0.9rem' }}>
              {getDistrictName(district.name)}
            </div>
          </button>
        ))}
      </div>
    </div>
  );
};

export default IconBasedNavigation;
