import React, { useState, useEffect } from 'react';
import { useLanguage } from '../utils/LanguageContext';
import { api } from '../services/api';
import { Bar } from 'react-chartjs-2';

const DistrictComparison = () => {
  const [districts, setDistricts] = useState([]);
  const [district1, setDistrict1] = useState('');
  const [district2, setDistrict2] = useState('');
  const [data1, setData1] = useState(null);
  const [data2, setData2] = useState(null);
  const [loading, setLoading] = useState(false);
  const { t, language, getDistrictName } = useLanguage();

  useEffect(() => {
    loadDistricts();
  }, []);

  const loadDistricts = async () => {
    try {
      const response = await api.getDistricts();
      setDistricts(response.data);
    } catch (error) {
      console.error('Failed to load districts:', error);
    }
  };

  const compareDistricts = async () => {
    if (!district1 || !district2) return;

    setLoading(true);
    try {
      const [response1, response2] = await Promise.all([
        api.getDistrictData(district1),
        api.getDistrictData(district2)
      ]);

      setData1(response1.data[0] || {});
      setData2(response2.data[0] || {});
    } catch (error) {
      console.error('Comparison failed:', error);
    } finally {
      setLoading(false);
    }
  };

  const getScaledValue = (value, type) => {
    switch(type) {
      case 'days': return (value || 0) * 10; // Scale up days
      case 'funds': return (value || 0) / 1000; // Scale down funds to thousands
      case 'workers': return (value || 0) / 100; // Scale down workers to hundreds
      default: return value || 0;
    }
  };

  const chartData = {
    labels: [t('jobsProvided'), t('avgDays'), t('fundsDisburse'), t('beneficiaries')],
    datasets: [
      {
        label: getDistrictName(district1),
        data: [
          data1?.total_households_worked || 0,
          getScaledValue(data1?.avg_days_employment, 'days'),
          getScaledValue(data1?.total_exp, 'funds'),
          getScaledValue(data1?.total_active_workers, 'workers')
        ],
        backgroundColor: 'rgba(52, 152, 219, 0.8)',
        borderColor: 'rgba(52, 152, 219, 1)',
        borderWidth: 2
      },
      {
        label: getDistrictName(district2),
        data: [
          data2?.total_households_worked || 0,
          getScaledValue(data2?.avg_days_employment, 'days'),
          getScaledValue(data2?.total_exp, 'funds'),
          getScaledValue(data2?.total_active_workers, 'workers')
        ],
        backgroundColor: 'rgba(231, 76, 60, 0.8)',
        borderColor: 'rgba(231, 76, 60, 1)',
        borderWidth: 2
      }
    ]
  };

  return (
    <div className="main-content">
      <div className="section-header">
        <h2>{language === 'ta' ? 'மாவட்ட ஒப்பீடு' : 'District Comparison'}</h2>
      </div>

      <div className="district-selector">
        <div className="comparison-selectors">
          <div className="form-group">
            <label className="form-label">{language === 'ta' ? 'முதல் மாவட்டம்' : 'First District'}</label>
            <select 
              className="district-dropdown"
              value={district1}
              onChange={(e) => setDistrict1(e.target.value)}
            >
              <option value="">-- {t('selectDistrict')} --</option>
              {districts.map(district => (
                <option key={district} value={district}>{getDistrictName(district)}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label className="form-label">{language === 'ta' ? 'இரண்டாம் மாவட்டம்' : 'Second District'}</label>
            <select 
              className="district-dropdown"
              value={district2}
              onChange={(e) => setDistrict2(e.target.value)}
            >
              <option value="">-- {t('selectDistrict')} --</option>
              {districts.map(district => (
                <option key={district} value={district}>{getDistrictName(district)}</option>
              ))}
            </select>
          </div>
        </div>

        <button 
          className="btn btn-primary"
          onClick={compareDistricts}
          disabled={!district1 || !district2 || loading}
        >
          {loading ? t('loading') : (language === 'ta' ? 'ஒப்பிடுக' : 'Compare')}
        </button>
      </div>

      {data1 && data2 && (
        <div className="chart-container">
          <Bar data={chartData} options={{
            responsive: true,
            plugins: {
              title: {
                display: true,
                text: language === 'ta' ? 'மாவட்ட செயல்திறன் ஒப்பீடு' : 'District Performance Comparison',
                font: { size: 18 }
              },
              legend: {
                position: 'top'
              },
              tooltip: {
                callbacks: {
                  label: function(context) {
                    const label = context.dataset.label || '';
                    const dataIndex = context.dataIndex;
                    const isDistrict1 = context.datasetIndex === 0;
                    const district = isDistrict1 ? data1 : data2;

                    let value;
                    switch(dataIndex) {
                      case 0: value = district?.total_households_worked || 0; break;
                      case 1: value = (district?.avg_days_employment || 0).toFixed(1) + ' days'; break;
                      case 2: value = '₹' + (district?.total_exp || 0).toLocaleString(); break;
                      case 3: value = (district?.total_active_workers || 0).toLocaleString(); break;
                      default: value = context.parsed.y;
                    }

                    return `${label}: ${value}`;
                  }
                }
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                title: {
                  display: true,
                  text: language === 'ta' ? 'அளவு (ஒப்பீட்டு அளவில்)' : 'Values (Scaled for Comparison)'
                }
              }
            }
          }} />
        </div>
      )}
    </div>
  );
};

export default DistrictComparison;
