import React from 'react';
import { useLanguage } from '../utils/LanguageContext';

const AudioTest = () => {
  const { language, speak } = useLanguage();

  const testAudio = () => {
    const testText = language === 'ta' ? 
      'வழங்கப்பட்ட வேலைகளின் எண்ணிக்கை ஐந்து' : 
      'Number of jobs provided is five';

    speak(testText, true, '5');
  };

  const testResponsiveVoice = () => {
    if (window.responsiveVoice) {
      const text = language === 'ta' ? 
        'வழங்கப்பட்ட வேலைகளின் எண்ணிக்கை ஐந்து' : 
        'Number of jobs provided is five';

      window.responsiveVoice.speak(text, language === 'ta' ? 'Hindi Female' : 'US English Female');
    } else {
      alert('ResponsiveVoice not loaded');
    }
  };

  return (
    <div style={{ padding: '20px', background: 'white', margin: '20px', borderRadius: '8px' }}>
      <h3>Audio Test</h3>
      <button onClick={testAudio} style={{ padding: '10px 20px', margin: '10px', background: '#3498db', color: 'white', border: 'none', borderRadius: '5px' }}>
        Test Context Audio
      </button>
      <button onClick={testResponsiveVoice} style={{ padding: '10px 20px', margin: '10px', background: '#27ae60', color: 'white', border: 'none', borderRadius: '5px' }}>
        Test ResponsiveVoice Direct
      </button>
      <p>Current Language: {language}</p>
      <p>ResponsiveVoice Status: {window.responsiveVoice ? 'Loaded' : 'Not Loaded'}</p>
    </div>
  );
};

export default AudioTest;
